let normalizedURL = normalizeURL(currentUrl);
function normalizeURL(url) {
    // Split the URL into the scheme part and the rest of the URL
    let parts = url.split('://');

    // If the URL doesn't contain a scheme part, just normalize the whole URL
    if (parts.length === 1) {
        return parts[0].replace(/\/{2,}/g, '/');
    }

    let scheme = parts[0] + '://';
    let restOfURL = parts[1].replace(/\/{2,}/g, '/');

    return scheme + restOfURL;
}

